// Next
import Link from "next/link";
import Image from "next/image";

// React
import PropTypes from "prop-types";

// Styles
import styles from "@/styles/components/ui/anchor.module.scss";

interface Props {
  type: "start" | "create";
  href: string;
}

/*******************************/
// type = one of the above options
// href = link to page (usually unique id)
/*******************************/

export default function Anchor({ type, href }: Props) {
  function btnText(type: string) {
    switch (type) {
      case "start":
        return "Start Test";
      case "create":
        return "Create Test";
    }
  }

  function btnImage(type: string) {
    switch (type) {
      case "start":
        return "data:image/svg+xml;base64,";
      case "create":
        return "data:image/svg+xml;base64,";
    }
  }

  //********************************/
  //***** USER FACING BELOW ******//
  //******************************/
  return (
    <Link className={styles.anchor} href={href}>
      {btnText(type)}
      <Image
        className={styles.anchor__image}
        src={btnImage(type) as string}
        alt={type}
        width={type === "start" ? 18 : 26}
        height={type === "start" ? 19 : 26}
        loading="eager"
      />
    </Link>
  );
}

Anchor.propTypes = {
  href: PropTypes.string.isRequired,
  type: PropTypes.string.isRequired,
};
