// Next
import Image from "next/image";

// React
import PropTypes from "prop-types";

// Styles
import styles from "@/styles/components/ui/button.module.scss";

interface Props {
  type: "next" | "complete";
}

/*******************************/
// type = one of the above options
/*******************************/

export default function Button({ type }: Props) {
  function btnText(type: string) {
    switch (type) {
      case "next":
        return "Next Question";
      case "complete":
        return "Complete Test";
    }
  }

  function btnImage(type: string) {
    switch (type) {
      case "next":
        return "data:image/svg+xml;base64,";
      case "complete":
        return "data:image/svg+xml;base64,";
    }
  }

  //********************************/
  //***** USER FACING BELOW ******//
  //******************************/
  return (
    <button className={styles.button}>
      {btnText(type)}
      <Image
        className={styles.button__image}
        src={btnImage(type) as string}
        alt={type}
        width={type === "next" ? 54 : 26}
        height={type === "next" ? 21 : 26}
        loading="eager"
      />
    </button>
  );
}

Button.propTypes = {
  type: PropTypes.string.isRequired,
};
